import React, { useState } from 'react'
import DatePicker from 'react-datepicker'
import moment from 'moment'
import Fieldset, { withFieldset, useFieldset } from 'react-fieldset'
import 'react-datepicker/dist/react-datepicker.css'
import './DataFormulario.css'

function DataFormulario() {
    const [checkInDate, setCheckInDate] = useState(null)
    const [checkOutDate, setCheckOutDate] = useState(null)

    const handleCheckInDate = (date) => {
        setCheckInDate(date)
        setCheckOutDate(null)
    }

    const handleCheckOutDate = (date) => {
        setCheckOutDate(date)
    }

    return (
        <Fieldset className="teste">
            <div className="App">
                <div className="input-container">
                    <div>
                        <label>Data de início</label>
                        <DatePicker
                            selected={checkInDate}
                            minDate={new Date()}
                            onChange={handleCheckInDate}
                        />
                    </div>
                    <div>
                        <label>Data fim</label>
                        <DatePicker
                            selected={checkOutDate}
                            minDate={checkInDate}
                            onChange={handleCheckOutDate}
                        />
                    </div>
                </div>
                {checkInDate && checkOutDate && (
                    <div className="summary">
                        <p>
                            Período selecionado:{' '}
                            {moment(checkInDate).format('LL')} to{' '}
                            {moment(checkOutDate).format('LL')}.
                        </p>
                    </div>
                )}
            </div>
        </Fieldset>
    )
}

export default DataFormulario
