import React from "react";
import ReactMultiSelectCheckboxes from "./CheckMeses";
import DateForm from "./DateForm";

import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';




export default function RadioB() {
  const [currentRadioValue, setCurrentValue] = React.useState("on");

  const handleRadioChange = (value) => {
    setCurrentValue(value);
  };
  return (
    <div>
      <FormControl component="fieldset">
      <FormLabel component="legend"></FormLabel>
      <RadioGroup row aria-label="period" name="row-radio-buttons-group">
        <FormControlLabel value="exercicio" control={<Radio />} label="Exercício" onChange={(e) => setCurrentValue(e.target.value)} defaultChecked={currentRadioValue === "on"}/>
        <FormControlLabel value="periodo" control={<Radio />} label="Período" onChange={(e) => setCurrentValue(e.target.value)}
            defaultChecked={currentRadioValue === "off"}/>

      </RadioGroup>
    </FormControl>
           {currentRadioValue === "on" && (
          <div>
            <h5 className="lgenda"> Escolha um ano de interesse</h5>
            <input placeholder="2021" type="number" min={2000} max={2030} />
            <h5 className="lgenda"> Escolha um mês de interesse</h5>
            <ReactMultiSelectCheckboxes className=""></ReactMultiSelectCheckboxes>
          </div>
        )}

            {currentRadioValue === "off" && (
          <div>
            {" "}
            <DateForm></DateForm>
          </div>
        )}
    </div>
  );
}
