import React from "react";
import ReactMultiSelectCheckboxes from "./CheckMeses.jsx";
import DateForm from "./DateForm.jsx";


export default function RadioB() {
  const [currentRadioValue, setCurrentValue] = React.useState("on");

  const handleRadioChange = (value) => {
    setCurrentValue(value);
  };
  return (
    <div>
      <>
        <div>
          <input
            name="radio-item-1"
            value="on"
            type="radio"
            onChange={(e) => setCurrentValue(e.target.value)}
            defaultChecked={currentRadioValue === "on"}
          />
          <label htmlFor="radio-item-12" className="radio-item-12">
            Exercício
          </label>
        </div>
        <div>
          <input
            name="radio-item-1"
            value="off"
            type="radio"
            onChange={(e) => setCurrentValue(e.target.value)}
            defaultChecked={currentRadioValue === "off"}
          />
          <label htmlFor="radio-item-22" className="radio-item-22">
            Período
          </label>
        </div>

        {currentRadioValue === "on" && (
          <div>
            <h5 className="lgenda"> Escolha um ano de interesse</h5>
            <input placeholder="2021" type="number" min={2000} max={2030} />
            <h5 className="lgenda"> Escolha um mês de interesse</h5>
            <ReactMultiSelectCheckboxes className=""></ReactMultiSelectCheckboxes>
          </div>
        )}

        {currentRadioValue === "off" && (
          <div>
            {" "}
            <DateForm></DateForm>
          </div>
        )}
      </>
    </div>
  );
}
